mapper.select:
SELECT user.username user_username, user.id user_id, orders.date order_date, orders.address order_address, orders.order_number order_number, order_detail.number, order_detail.total_price, order_detail.status, order_detail.id, goods.name goods_name, goods.id goods_id, merchant.id merchant_id, merchant.store_name merchant_store_name FROM order_detail INNER JOIN orders ON order_detail.order_id = orders.id INNER JOIN user ON orders.user_id = user.id INNER JOIN goods ON order_detail.goods_id = goods.id INNER JOIN merchant ON order_detail.merchant_id = merchant.id
AND order_detail.merchant_id = #{merchantId}
AND user.id = #{userId}
AND order_detail.status != 'DELETED'
AND order_detail.status = #{status}
AND orders.date >= #{startDate}
AND orders.date <= #{endDate}
AND user.username LIKE "%"#{keywords}"%"
AND orders.order_number LIKE "%"#{keywords2}"%"
ORDER BY ${orderField} ${orderDirection}
order by orders.date DESC

select status
from order_detail
where id = #{id}
